import React from "react";
import { StyleSheet, View , Text} from "react-native";
import MapView from "react-native-maps";
import MapViewDirections from 'react-native-maps-directions';
import CountDown from 'react-native-countdown-component';
 
import {
  Button,
  Actionsheet,
  useDisclose,
  Box,
  extendTheme,
  Avatar,
  HStack,
  VStack
} from 'native-base';

const origin = {latitude: -29.98131942375116, longitude: -71.35180660362076};
const destination = {latitude: -29.965314, longitude: -71.349513};
const GOOGLE_MAPS_APIKEY = '';

export default function TripScreen() {
  const {
    isOpen,
    onOpen,
    onClose
  } = useDisclose();
  return (
      <View style={styles.container}>
        {}
        <MapView
          style={styles.map}
          initialRegion={{
            latitude: -29.98131942375116,
            longitude: -71.35180660362076,
            latitudeDelta: 0.0922,
            longitudeDelta: 0.0421,
          }
          
        }>
          <MapView.Marker
            coordinate={{
              latitude: -29.98131942375116,
              longitude: -71.35180660362076
            }}
            
            image={{uri: 'data:image/png;base64,'}}
            title={"title"}
            description={"description"}
        />
          <MapView.Marker
            pinColor={'blue'}
            coordinate={{
              latitude: -29.965314,
              longitude: -71.349513
            }}
            image={{uri: 'data:image/png;base64,'}}
            title={"UCN"}
            description={"Campus CBO"}
        />
        <MapViewDirections
          origin={origin}
          destination={destination}
          apikey={GOOGLE_MAPS_APIKEY}
        />
        </MapView>
        <Button onPress={onOpen}>Actionsheet</Button>
        <Actionsheet isOpen={true} onClose={onClose} >
          <Actionsheet.Content justifyContent="center">
            
            <Box w="100%" h={60} px={4} justifyContent="center">
              <Text style={styles.H1}>Confirmar punto de partida</Text>
            </Box>
            <Actionsheet.Item >
              <HStack justifyContent="center" mx={{
              base: "5",
              md: "2"
              }} space={2}>
                  <Avatar bg="green.500" mr="4" Avatar size="lg" marginTop={8} marginLeft={0} ource={{
                  uri: "https://bit.ly/broken-link"
                  }}>
                    NG
                  </Avatar>

                  <VStack space={2} alignItems="center">
                      <Text style={styles.H2}>Inicio de viaje</Text>
                      <Text style={styles.adress}>El faro 15, La Herradura</Text>
                      <Text></Text>
                      <Text style={styles.H2}>Destino</Text>
                      <Text style={styles.location}>UCN Campus Guayacan</Text>
                      <Text style={styles.adress}>Larrondo 1281, Coquimbo</Text>
                  </VStack>
                  <VStack space={1} alignItems="center" marginTop={10}>
                    <CountDown
                      until={10 * 60 +30}
                      timeToShow={['M', 'S']}
                      onFinish={() => alert('finished')}
                      onPress={() => alert('hello')}
                      digitStyle={{backgroundColor: '#FFF'}}
                      timeLabels={{m: 'MM', s: 'SS'}}
                      size={20}
                    />
                  </VStack>
                </HStack>
            </Actionsheet.Item>
            <Actionsheet.Item>
              <VStack alignItems="center">
                <Button backgroundColor={"#002333"} w="100%"
                 padding={5} marginTop={1} marginLeft={55} borderRadius={15}>
                  Comenzar viaje
                  </Button>
              </VStack>
            </Actionsheet.Item>
          </Actionsheet.Content>
        </Actionsheet>
        </View>

  );
}

//create our styling code:
const styles = StyleSheet.create({
  container: {
    ...StyleSheet.absoluteFillObject,
    flex: 1, //the container will fill the whole screen.
    justifyContent: "flex-end",
    alignItems: "center",
  },
  map: {
    ...StyleSheet.absoluteFillObject,
  },
  H1: {
    fontSize:22,
    flex: 1,
    flexDirection: 'column',
    fontWeight: 'bold' ,
    //backgroundColor: 'red',
    justifyContent: 'center',
    alignItems: 'center',
    padding: 50,
},
  H2: {
    fontSize:14,
    fontWeight: 'bold' ,

  },
  location: {
    fontSize:10,
    fontStyle: 'italic',
    fontWeight: 'bold' ,

  },
  destination: {
    fontSize:12,
  },
  adress: {
    fontSize:12,
    fontStyle: 'italic',
    fontWeight: '300' ,
  },
  map: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
  }
});
